import React from "react";
import "./CRUDAdmin.css";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Table,
  Button,
  Container,
  Modal,
  ModalHeader,
  ModalBody,
  FormGroup,
  ModalFooter,
  Input
} from "reactstrap";

class CRUDAdmin extends React.Component {
  state = {
    data: [],
    dataFiltrada: [],
    modalActualizar: false,
    modalInsertar: false,
    modalEliminar: false,
    administradorAEliminar: '',
    formInsertar: {
      noCuentaAdmin: "",
      nombre: "",
      apellido: "",
      email: "",
      psswd: ""
    },
    
    formActualizar: {
      noCuentaAdmin: "",
      nombre: "",
      apellido: "",
      email: "",
      psswd: ""
    },
    busqueda: "",
    errorsInsertar: {},
    errorsEditar: {},
  };

  componentDidMount() {
    fetch("http://127.0.0.1:5000/admin/readadmin")
      .then((response) => response.json())
      .then((data) => {
        this.setState({ data: data, dataFiltrada: data });
      })
      .catch((error) => {
        alert("Error al obtener datos del servidor:", error);
      });
  }

  mostrarTodosStands = () => {
    // Realiza la redirección a la nueva página
    window.location.href = "http://localhost:3000/vistaStand";
  };


  mostrarModalActualizar = (dato) => {
    this.setState({
      formActualizar: dato,
      modalActualizar: true,
    });
  };

  cerrarModalActualizar = () => {
    this.setState({ errorsEditar: {} });
    this.setState({ modalActualizar: false });
  };

  mostrarModalInsertar = () => {
    this.setState({
      modalInsertar: true,
    });
  };

  cerrarModalInsertar = () => {
    this.setState({ errorsInsertar: {} });
    this.setState({ modalInsertar: false });
  };

  mostrarModalEliminar = (dato) => {
    this.setState({
      modalEliminar: true,
      administradorAEliminar: dato.noCuentaAdmin
    });
  }

  cerrarModalEliminar = () => {
    this.setState({ modalEliminar: false });
  }

  insertar = async () => {
    if (!this.datosValidosInsertar()) {
      return;
    }
  
    const { noCuentaAdmin, nombre, apellido, email, psswd } = this.state.formInsertar;
    const noCuentaSupAdm = localStorage.getItem('noCuenta');
  
    try {
      const response = await fetch("http://127.0.0.1:5000/admin/insertadmin", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ noCuentaAdmin, nombre, apellido, email, psswd, noCuentaSupAdm }),
      });
  
      const data = await response.json();
  
      if (response.ok && data.message === "Administrador insertado correctamente") {
        // Registro exitoso
        this.componentDidMount(); // Recargar datos
        this.cerrarModalInsertar(); // Cerrar modal
      } else {
        // Manejar errores de la respuesta
        const errorsInsertar = {};
  
        if (data.error === "Error, correo asociado a otra cuenta.") {
          errorsInsertar["email"] = "El correo ya está asociado a otra cuenta.";
        } else if (data.error === "Error, número de cuenta asociado a otro usuario.") {
          errorsInsertar["noCuentaAdmin"] = "El número de cuenta ya está registrado.";
        } else {
          errorsInsertar["general"] = "Error desconocido. Por favor, inténtalo nuevamente.";
        }
  
        // Actualizar estado con los errores
        this.setState({ errorsInsertar });
        return; // Detener ejecución
      }
    } catch (error) {
      console.error("Error al insertar administrador:", error);
      this.setState({
        errorsInsertar: {
          general: "Error de conexión. Por favor, inténtalo más tarde.",
        },
      });
    }
  };

  editar = async () => {
    if (!this.datosValidosEditar()) {
      return;
    }
    const {noCuentaAdmin, nombre, apellido, email} = this.state.formActualizar; 
    const psswd = this.state.formActualizar.psswd || "Contra";
    try{    
      const response = fetch("http://127.0.0.1:5000/admin/updateadmin", {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({noCuentaAdmin, nombre, apellido, email, psswd}),
      });

      const data = await response.json();
  
      if (response.ok && data.message === "Administrador insertado correctamente") {
        // Registro exitoso
        this.componentDidMount(); // Recargar datos
        this.cerrarModalActualizar(); // Cerrar modal
      } else {
        // Manejar errores de la respuesta
        const errorsEditar = {};
  
        if (data.error === "Error, correo asociado a otra cuenta.") {
          errorsEditar["email"] = "El correo ya está asociado a otra cuenta.";
        } else {
          errorsEditar["general"] = "Error desconocido. Por favor, inténtalo nuevamente.";
        }
  
        // Actualizar estado con los errores
        this.setState({ errorsEditar });
        return; // Detener ejecución
      } 
    }  
    catch (error) {
      console.error("Error al editar administrador:", error);
      this.setState({
        errorsEditar: {
          general: "Error de conexión. Por favor, inténtalo más tarde.",
        },
      });
    }
  };

  eliminar = (dato) => {
    fetch("http://127.0.0.1:5000/admin/deleteadmin", {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ noCuentaAdmin: dato.noCuentaAdmin }),
    })
      .then(response => response.json())
      .then(data => {
        if (data.message) {
          this.componentDidMount();
          this.setState({ modalEliminar: false, administradorAEliminar: null });
        } else {
          alert('Error al eliminar administrador en el servidor: ' + data.error);
        }
      })
      .catch(error => {
        console.error("Error al eliminar administrador:", error);
        alert("Error al eliminar administrador. Por favor, inténtalo más tarde.");
      });
  };

  
  handleChangeInsertar = (e) => {
    this.setState({
      formInsertar: {
        ...this.state.formInsertar,
        [e.target.name]: e.target.value,
      },
    });
  };

  handleChangeActualizar = (e) => {
    this.setState({
      formActualizar: {
        ...this.state.formActualizar,
        [e.target.name]: e.target.value,
      },
    });
  };

  handleChangeBuscar = async (e) => {
    e.persist();
    await this.setState({ busqueda: e.target.value });
    this.filtrarElementos();
  }

  filtrarElementos = () => {
    var search = this.state.dataFiltrada.filter(item => {
      return (
        item.noCuentaAdmin.toString().includes(this.state.busqueda) ||
        item.nombre.toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, "").includes(this.state.busqueda.toLowerCase()) ||
        item.apellido.toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, "").includes(this.state.busqueda.toLowerCase()) ||
        item.email.toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, "").includes(this.state.busqueda.toLowerCase())
      );
    });
    this.setState({ data: search });
  }

  datosValidosInsertar = () => {
    let errorsInsertar = {};
    let isValid = true;

    if (this.state.formInsertar.noCuentaAdmin.length !== 9) {
      isValid = false;
      errorsInsertar["noCuentaAdmin"] = "Número de cuenta no válido.";
    }

    if(!this.state.formInsertar.nombre){
      isValid = false;
      errorsInsertar["nombre"] = "Por favor, ingresa un nombre.";
    }else{
      if (this.state.formInsertar.nombre.length < 3){
        isValid = false;
        errorsInsertar["nombre"] = "El nombre debe de tener al menos 3 carácteres.";
      }
    }
    
    if(!this.state.formInsertar.apellido){
      isValid = false;
      errorsInsertar["apellido"] = "Por favor, ingresa un apellido.";
    }else{
      if (this.state.formInsertar.apellido.length < 3){
        isValid = false;
        errorsInsertar["apellido"] = "El apellido debe de tener al menos 3 carácteres.";
      }
    } 

    if (!this.state.formInsertar.email) {
      isValid = false;
      errorsInsertar["email"] = "Por favor ingresa un correo.";
    } else {
      if (typeof this.state.formInsertar.email !== "undefined") {
        let lastAtPos = this.state.formInsertar.email.lastIndexOf('@');
        let lastDotPos = this.state.formInsertar.email.lastIndexOf('.');

        if (!(lastAtPos < lastDotPos && lastAtPos > 0 && this.state.formInsertar.email.indexOf('@@') === -1 && lastDotPos > 2 && (this.state.formInsertar.email.length - lastDotPos) > 2)) {
          isValid = false;
          errorsInsertar["email"] = "Correo no válido.";
        }
      }
    }

    if (!this.state.formInsertar.psswd) {
      isValid = false;
      errorsInsertar["password"] = "Por favor ingresa una contraseña.";
    } else if (this.state.formInsertar.psswd.length < 8) {
      isValid = false;
      errorsInsertar["password"] = "La contraseña debe tener al menos 8 caracteres.";
    } 

    this.setState({ errorsInsertar });
    return isValid;
  }

  datosValidosEditar = () => {
    let errorsEditar = {};
    let isValid = true;

    if(!this.state.formActualizar.nombre){
      isValid = false;
      errorsEditar["nombre"] = "Por favor, ingresa un nombre.";
    }else{
      if (this.state.formActualizar.nombre.length < 3){
        isValid = false;
        errorsEditar["nombre"] = "El nombre debe de tener al menos 3 carácteres.";
      }
    }
    
    if(!this.state.formActualizar.apellido){
      isValid = false;
      errorsEditar["apellido"] = "Por favor, ingresa un apellido.";
    }else{
      if (this.state.formActualizar.apellido.length < 3){
        isValid = false;
        errorsEditar["apellido"] = "El apellido debe de tener al menos 3 carácteres.";
      }
    } 

    if (this.state.formActualizar.email) {      
      if (typeof this.state.formActualizar.email !== "undefined") {
        let lastAtPos = this.state.formActualizar.email.lastIndexOf('@');
        let lastDotPos = this.state.formActualizar.email.lastIndexOf('.');

        if (!(lastAtPos < lastDotPos && lastAtPos > 0 && this.state.formActualizar.email.indexOf('@@') === -1 && lastDotPos > 2 && (this.state.formActualizar.email.length - lastDotPos) > 2)) {
          isValid = false;
          errorsEditar["email"] = "Correo inválido.";
        }
      }
    }

    if (this.state.formActualizar.psswd && this.state.formActualizar.psswd.length < 8) {
      isValid = false;
      errorsEditar["password"] = "La nueva contraseña debe tener al menos 8 caracteres.";
    } 

    this.setState({ errorsEditar });
    return isValid;
  }


  render() {
    return (
      <>
        <Container className="CRUDAdmin">
          <br />
          <td>
          <div className="d-flex justify-content-between">
            <Button style={{width: "200px", marginRight: "10px" }}  color="success" onClick={() => this.mostrarModalInsertar()}>Nuevo administrador</Button>
            <Button style={{width: "200px", marginRight: "10px" }}  color="success" onClick={() => this.mostrarTodosStands()}>Ver stands registrados</Button>
          </div>
          </td>
          <div className="barraBusqueda">
          <FormGroup className="d-flex align-items-center">
              <img src="lupa.png" alt="Ícono de búsqueda" style={{ height: '26px', marginRight: '10px'}} />{" "}
              <Input
                type="text"
                placeholder="Buscar"
                className="textField"
                name="busqueda"
                value={this.state.busqueda}
                onChange={this.handleChangeBuscar}
              />
          </FormGroup>
          </div>
          <br />
          <br />
          <Table>
            <thead>
              <tr>
                <th>No. Cuenta</th>
                <th>Nombre</th>
                <th>Apellido</th>
                <th>Email</th>
                <th>Stands registrados</th>
                <th>Acción</th>
              </tr>
            </thead>

            <tbody>
              {this.state.data.map((dato) => (
                <tr key={dato.noCuentaAdmin}>
                  <td>{dato.noCuentaAdmin}</td>
                  <td>{dato.nombre}</td>
                  <td>{dato.apellido}</td>
                  <td>{dato.email}</td>
                  <td>{dato.stands}</td>
                  <td>
                    <Button
                      style={{ width: "150px", display: "block", marginBottom: "10px" }}
                      color="primary"
                      onClick={() => this.mostrarModalActualizar(dato)}
                    >Editar
                    </Button>{" "}
                    <Button style={{ width: "150px", display: "block", marginBottom: "10px", 
                    backgroundColor: '#F05E16', borderColor: '#F05E16', transition: 'background-color 0.3s ease' }} 
                    onClick={() => this.mostrarModalEliminar(dato)}onMouseOver={(e) => e.target.style.backgroundColor = '#B05625'}
                    onMouseOut={(e) => e.target.style.backgroundColor = '#F05E16'}>Eliminar</Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Container>


        <Modal isOpen={this.state.modalActualizar}>
          <ModalHeader>
            <div><h3>Editar Administrador</h3></div>
          </ModalHeader>

          <ModalBody>
            <FormGroup>
              <label>
                Nombre:
              </label>

              <input
                className="form-control"
                name="nombre"
                type="text"
                onChange={this.handleChangeActualizar}
                value={this.state.formActualizar.nombre}
              />{
                this.state.errorsEditar.nombre && <div className="alert alert-danger">
                  {this.state.errorsEditar.nombre}
                </div>
              }
            </FormGroup>

            <FormGroup>
              <label>
                Apellido:
              </label>
              <input
                className="form-control"
                name="apellido"
                type="text"
                onChange={this.handleChangeActualizar}
                value={this.state.formActualizar.apellido}
              />
              {
                this.state.errorsEditar.apellido && <div className="alert alert-danger">
                  {this.state.errorsEditar.apellido}
                </div>
              }
            </FormGroup>

            <FormGroup>
              <label>
                Email:
              </label>
              <input
                className="form-control"
                name="email"
                type="email"
                onChange={this.handleChangeActualizar}
                value={this.state.formActualizar.email}                
              />
              {
                this.state.errorsEditar.email && <div className="alert alert-danger">
                  {this.state.errorsEditar.email}
                </div>
              }
            </FormGroup>

            <FormGroup>
              <label>
                Contraseña:
              </label>
              <input
                className="form-control"
                name="psswd"
                type="password"
                onChange={this.handleChangeActualizar}
                value={this.state.formActualizar.psswd}                               
              />
              {
                this.state.errorsEditar.password && <div className="alert alert-danger">
                  {this.state.errorsEditar.password}
                </div>
              }
            </FormGroup>
          </ModalBody>

          <ModalFooter>
            <Button
              style={{ width: "150px"}} color="primary"
              onClick={() => this.editar()}
            >
              Editar
            </Button>
            <Button
              style={{ width: "150px"}} color="secondary"
              onClick={() => this.cerrarModalActualizar()}
            >
              Cancelar
            </Button>
          </ModalFooter>
        </Modal>

        <Modal isOpen={this.state.modalInsertar}>
          <ModalHeader>
            <div><h3>Insertar Administrador</h3></div>
          </ModalHeader>

          <ModalBody>

          <FormGroup>
              <label>
                No de cuenta:
              </label>

              <input
                className="form-control"
                name="noCuentaAdmin"
                type="number"
                onChange={this.handleChangeInsertar}
              />
               {
                this.state.errorsInsertar.noCuentaAdmin && <div className="alert alert-danger">
                  {this.state.errorsInsertar.noCuentaAdmin}
                </div>
              }
            </FormGroup>

            <FormGroup>
              <label>
                Nombre:
              </label>

              <input
                className="form-control"
                name="nombre"
                type="text"
                onChange={this.handleChangeInsertar}
                required
              />
              {
                this.state.errorsInsertar.nombre && <div className="alert alert-danger">
                  {this.state.errorsInsertar.nombre}
                </div>
              }
            </FormGroup>

            <FormGroup>
              <label>
                Apellido:
              </label>
              <input
                className="form-control"
                name="apellido"
                type="text"
                onChange={this.handleChangeInsertar}
                required
              />
              {
                this.state.errorsInsertar.apellido && <div className="alert alert-danger">
                  {this.state.errorsInsertar.apellido}
                </div>
              }
            </FormGroup>

            <FormGroup>
              <label>
                Email:
              </label>
              <input
                className="form-control"
                name="email"
                type="email"
                onChange={this.handleChangeInsertar}
                required
              />
              {
                this.state.errorsInsertar.email && <div className="alert alert-danger">
                  {this.state.errorsInsertar.email}
                </div>
              }
            </FormGroup>

            <FormGroup>
              <label>
                Contraseña:
              </label>
              <input
                className="form-control"
                name="psswd"
                type="password"
                onChange={this.handleChangeInsertar}
                required
              />
              {
                this.state.errorsInsertar.password && <div className="alert alert-danger">
                  {this.state.errorsInsertar.password}
                </div>
              }
            </FormGroup>
          </ModalBody>

          <ModalFooter>
            <Button
              style={{ width: "150px"}} color="primary" 
              onClick={() => this.insertar()}
            >
              Insertar
            </Button>
            <Button
              style={{ width: "150px"}} color="secondary"
              onClick={() => this.cerrarModalInsertar()}
            >
              Cancelar
            </Button>
          </ModalFooter>
        </Modal>

        <Modal isOpen={this.state.modalEliminar}>
          <ModalHeader>
            <div><h3>Eliminar Administrador</h3></div>
          </ModalHeader>
          <ModalBody>
            <p>¿Está seguro de eliminar el administrador con No. de cuenta: {this.state.administradorAEliminar}?</p>
          </ModalBody>

          <ModalFooter>
            <Button style={{ width: "150px", display: "block", marginBottom: "10px", 
                    backgroundColor: '#F05E16', borderColor: '#F05E16', transition: 'background-color 0.3s ease' }} 
                    onClick={() => this.eliminar({ noCuentaAdmin: this.state.administradorAEliminar })}  
                    onMouseOver={(e) => e.target.style.backgroundColor = '#B05625'}
                    onMouseOut={(e) => e.target.style.backgroundColor = '#F05E16'}>
              Confirmar
            </Button>
            <Button style={{ width: "150px"}} color="secondary"  onClick={() => this.setState({ modalEliminar: false, administradorAEliminar: null })}>
              Cancelar
            </Button>
          </ModalFooter>
        </Modal>
      </>
    );
  }
}

export default CRUDAdmin;
